SELECT a.node_id, a.node_title, a.avatar_normal, a.avatar_large, a.node_desc, a.create_date, (SELECT COUNT(1) FROM topic b WHERE b.is_delete = 0 AND a.node_title = b.node_title) count_topic FROM node a WHERE a.is_delete = 0
AND a.node_title = #{nodeTitle}
