mapper.select:
select b.user_id, b.user_name, b.signature, b.avatar from visit a,user b where a.vid = #{vid} and a.is_delete = '0' and a.uid = b.user_id order by a.create_date desc
limit #{start,jdbcType=INTEGER}
,#{limit,jdbcType=INTEGER}

select count(1)
from visit
where uid = #{uid}
and vid = #{vid}
and is_delete = '0'

mapper.delete:
delete from visit
where uid = #{uid,jdbcType=INTEGER}
and vid = #{vid,jdbcType=INTEGER}

mapper.update:
update visit
set create_date = #{createDate,jdbcType=TIMESTAMP},
is_delete = #{isDelete,jdbcType=BIT}
where uid = #{uid,jdbcType=INTEGER}
and vid = #{vid,jdbcType=INTEGER}
